INSERT INTO public.responses (question_id, question_text, llm_response, original_answer, bert_score, processing_attempts, total_processing_time_ms, created_at, updated_at) VALUES ('a275e183d6ae5c79', 'how many miles is one light year?', 'Un a├▒o luz equivale aproximadamente a **5.879 billones de millas** (5.879 x 10^12 millas).', 'A light year (or light-year), is defined as the distance that a photon would travel, in free space and infinitely distant from any gravitational or magnetic fields, in one Julian year (365.25 days of 86,400 seconds each). Since the speed of light in a vacuum is exactly 299,792,458 m/s by the definition of metre, one light year is exactly equal to 9,460,730,472,580,800 m, or about 5.879 ├ù 10^12 (nearly six trillion) miles.', 0.6920855045318604, 1, 500, '2025-09-16 16:16:56.565294', '2025-09-16 16:16:56.565294');
