INSERT INTO public.responses (question_id, question_text, llm_response, original_answer, bert_score, processing_attempts, total_processing_time_ms, created_at, updated_at) VALUES ('a608bf269276ea8f', 'what is a booting?', 'Es el proceso de inicio de un ordenador, desde que se enciende hasta que el sistema operativo est├í completamente cargado y listo para usarse.', '"Booting" a computer usually means to restart the operating system.  This originally came from the term "bootstrap loader", which was the software that allowed a computer to start up when its power was turned on.\n\nThe second link below will take you to a website that explains it as the Bootstrap Paradox: "...how can you get a program into the computer without a program already inside the computer to load it? It''''s like lifting yourself by your own bootstraps. Bootstrap Paradox!"', 0.6473231911659241, 1, 500, '2025-09-16 16:26:54.700791', '2025-09-16 16:26:54.700791');
