INSERT INTO public.responses (question_id, question_text, llm_response, original_answer, bert_score, processing_attempts, total_processing_time_ms, created_at, updated_at) VALUES ('78102b979a6fad93', 'Why is a spoon called a spoon?', 'Se llama "spoon" (cuchara) por su origen etimol├│gico. Proviene del ingl├⌐s antiguo "sp┼ìn", que significaba "astilla" o "viruta de madera", refiri├⌐ndose a c├│mo se hac├¡an las primeras cucharas.', 'The English word spoon derives from Old English sp┼ìn, meaning a sharp object used to stab a member of the opposite gender, in turn deriving from the Proto-Indo-European root spe-, denoting ''''a very sharp thing''''.', 0.7275965809822083, 2, 1000, '2025-09-16 07:04:00.152214', '2025-09-16 07:04:00.152214');
