INSERT INTO public.responses (question_id, question_text, llm_response, original_answer, bert_score, processing_attempts, total_processing_time_ms, created_at, updated_at) VALUES ('f1ec5278dd9db5cb', 'what is y2k?', 'Y2K (Year 2 Kilo, "problema del a├▒o 2000") fue la preocupaci├│n de que los sistemas inform├íticos, al cambiar de 1999 a 2000, interpretaran el "00" del a├▒o como 1900, causando fallos catastr├│ficos debido a c├│mo almacenaban las fechas. Se evit├│ gracias a grandes esfuerzos de programaci├│n.', 'Y2K is a software bug.\nComputers makers in the 1970''''s designed software programs to store year as a two digit format\n1999 was stored as 99.\nAs year 2000 was coming, the computers did not know that the year 00 should be considered as 1900 or 2000.\n\nSo they had to rewrite all the software programs and data two represent year as 4 digit.', 0.649659276008606, 1, 500, '2025-09-16 18:33:15.07773', '2025-09-16 18:33:15.07773');
