INSERT INTO public.responses (question_id, question_text, llm_response, original_answer, bert_score, processing_attempts, total_processing_time_ms, created_at, updated_at) VALUES ('b4a0b481ed3ab6f1', 'what is the work of eyebrows?', 'Proteger los ojos del sudor, la lluvia y residuos, desvi├índolos.', 'While I think in the past scientific thought was that the eyebrows shaded the eyes and protected them from sweat, what I see is that they are critical for non-verbal communication.\n\nIn a hunter-gatherer society, men hunting could communicate across distances silently with head and facial gestures.  Eyebrows emphasize and exaggerate the actions of the muscles around the eyes.  Consider Spock''''s one raised eyebrow or Groucho Marx''''s leering waggle.', 0.6249739527702332, 1, 500, '2025-09-16 20:13:48.864018', '2025-09-16 20:13:48.864018');
