INSERT INTO public.responses (question_id, question_text, llm_response, original_answer, bert_score, processing_attempts, total_processing_time_ms, created_at, updated_at) VALUES ('8e7e39a6ce00b1c7', 'What is a gladiator?', 'Un gladiador era un combatiente que luchaba en espect├ículos p├║blicos en la Antigua Roma para el entretenimiento.', 'Gladiators (Latin gladiatores) were professional fighters in ancient Rome who fought against each other, wild animals, and slaves, sometimes to the death, for the entertainment of spectators. These fights took place in arenas in many cities during the Roman republic and the Roman Empire.\n\nThe word comes from gladius, the Latin word for a short sword used by legionaries and some gladiators.', 0.7296609878540039, 1, 500, '2025-09-16 22:28:31.675746', '2025-09-16 22:28:31.675746');
