INSERT INTO public.responses (question_id, question_text, llm_response, original_answer, bert_score, processing_attempts, total_processing_time_ms, created_at, updated_at) VALUES ('61ed8ca14b6902d1', 'what is coq10?', 'CoQ10 (coenzima Q10) es una sustancia similar a las vitaminas que el cuerpo produce naturalmente. Act├║a como antioxidante y es fundamental para la producci├│n de energ├¡a en las c├⌐lulas.', 'CO Q 10 10MG SOFTGELS \nIf you''''re taking your New Year''''s resolutions to heart, don''''t forget the Co Q-10! As a supplement, Coenzyme Q-10 has been shown to support heart health.** This naturally occurring, fat-soluble antioxidant appears in almost every cell in the body.** Antioxidants ΓÇö which include Vitamins C and E, Lycopene and Selenium ΓÇö play a role in fighting the cell-damaging effects of free radicals.**', 0.6698427796363831, 1, 500, '2025-09-16 22:47:54.286926', '2025-09-16 22:47:54.286926');
