INSERT INTO public.responses (question_id, question_text, llm_response, original_answer, bert_score, processing_attempts, total_processing_time_ms, created_at, updated_at) VALUES ('7eb831d5d3ac19cf', 'What are footballs made out of?', 'Principalmente de **materiales sint├⌐ticos** como el poliuretano o el PVC, y una c├ímara de aire de caucho en su interior.', 'Many years ago American footballs were made of pig bladders. However current NFL footballs are made by Wilson and are made from cowhide lined with a two ply butyl rubber bladder to make it airtight. Pig "skin" was actually never used. \n\nThe standard football, referred to by Americans as a soccer ball, is made of a polyurethane or polyvinyl chloride outer coat, with a rubber or synthetic rubber lining. Rumor has it that ancient Brits used human heads for footballs. That would be a tough game!', 0.6373780965805054, 1, 500, '2025-09-16 22:56:48.351518', '2025-09-16 22:56:48.351518');
